import torch
import torch.nn.functional as F
from torch import Tensor
from transformers import AutoTokenizer, AutoModel
from sentence_transformers import SentenceTransformer
from typing import List, Dict, Optional
from logger_config import get_logger
import numpy as np


class EmbeddingGeneration:
    """Class for generating embeddings using BGE model"""
    
    # BAAI/bge-base-en
    def __init__(self, max_len=768, model_name: str = 'nomic-ai/nomic-embed-text-v1', logger_instance=None):
        """Initialize the embedding generation class
        
        Args:
            model_name: Name of the BGE model to use
        """
        self.logger = logger_instance if logger_instance is not None else get_logger(__name__) # Use passed logger or default
        self.max_len = max_len  
        try:
            self.tokenizer = AutoTokenizer.from_pretrained(model_name)
            self.model = SentenceTransformer(model_name, trust_remote_code=True)
        except Exception as e:
            self.logger.error(f"Failed to load primary model {model_name}: {str(e)}")
            raise
    
    def last_token_pool(self, embeddings: Tensor) -> Tensor:
        """Pool the last token embeddings
        
        Args:
            last_hidden_states: Last hidden states from the model
            
        Returns:
            Tensor: Pooled embeddings
        """
        try:
            left_padding = (embeddings[:, -1].sum() == embeddings.shape[0])
            if left_padding:
                return embeddings[:, -1]
            else:
                sequence_lengths = embeddings.sum(dim=1) - 1
                batch_size = embeddings.shape[0]
                return embeddings[torch.arange(batch_size, device=embeddings.device), sequence_lengths]
        except Exception as e:
            self.logger.error(f"Error in last token pooling: {str(e)}")
            # Fallback to simple mean pooling
            return torch.mean(embeddings, dim=1)

    # def generate_embeddings(self, text: List[str]) -> tuple[Tensor, Tensor]:
    def generate_embeddings(self, text: List[str]) -> Tensor:
        """Generate embeddings for queries and documents
        
        Args:
            text: List of text strings
            
        Returns:
            Tensor: Document embeddings
        """
        try:
            with torch.no_grad():
                outputs = self.model.encode(text)
                text_embedding = torch.tensor(outputs)
                
            # normalize embeddings
            text_embedding = F.normalize(text_embedding, p=2, dim=1)
            
            return text_embedding
        except Exception as e:
            self.logger.error(f"Error generating embeddings: {str(e)}")

